package indexer;/** * This class represents a Post on Stack Overflow website * @author Narasimman * */public class Post {  private final int id;  private final String title;  private final String body;  private final int acceptedAnswerId;  public Post(int id, String title, String body, int aId) {    this.id = id;    this.title = title;    this.body = body;    this.acceptedAnswerId = aId;  }  public int getId() {    return id;  }  public String getTitle() {    return title;  }  public String getBody() {    return body;  }  public int getAcceptedAnswerId() {    return acceptedAnswerId;  }}