package indexer;/** * This class represents a Post on Stack Overflow website *  * @author Narasimman *  */public class Post {  private final int id;  private final String title;  private final String body;  private final int acceptedAnswerId;  private final int score;  private final int viewCount;  private final int favoriteCount;  private Answer ans;  public Post(int id, String title, String body, int aId, int score, int viewCount, int favCount) {    this.id = id;    this.title = title;    this.body = body;    this.acceptedAnswerId = aId;    this.score = score;    this.viewCount = viewCount;    this.favoriteCount = favCount;   }  public int getId() {    return id;  }  public String getTitle() {    return title;  }  public String getBody() {    return body;  }  public int getAcceptedAnswerId() {    return acceptedAnswerId;  }    public int getScore()  {	  return score;  }  public int getViewCount()  {	  return viewCount;  }  public int getFavCount()  {	  return favoriteCount;  }    public double getWeightScore()  {	  return (score * favoriteCount)/viewCount;  }    public Answer getAnsObj()  {	  return ans;  }  public void setAnsObj(Answer ans)  {	  this.ans = ans;  }           }